import React, {Component} from 'react';
import {connect} from 'react-redux';
import {Navbar} from 'react-bootstrap';
import { Link, withRouter } from "react-router-dom";
import { logout } from './actions/authActions';
import "./App.css"


class Navigation extends Component {

    handleLogout = (e) => {
        e.preventDefault();
        this.props.logout();
        this.props.history.push('/')
    }


    render() {



        const mainNav = (
           <Navbar defaultActiveKey="/">
                <Link to="/">PicList</Link> | 
              <Link to="/about"> About</Link> | 
              <Link to="/login"> Log In</Link> | 
              <Link to="/signup"> Sign Up</Link> 
              <h3>
                  PicList!
              </h3>
            </Navbar>
        )

        const userNav = (
            <Navbar fluid collapseOnSelect>
                <Navbar.Header>
                    <Navbar.Brand>
                        <Link to="/">PicList</Link> | 
                    </Navbar.Brand>
                        <Link to="/about"> About</Link> | 
                        <Link to="/saved_pics"> Saved Pics</Link> | 
                        <Link to="#" onClick={(e) => this.handleLogout(e)}> Log Out</Link>
                    <Navbar.Toggle />
                </Navbar.Header>               
            </Navbar>
        );


        return (
        <>

                {this.props.isAuthenticated ? userNav : mainNav}
                {userNav}

        </>
        )
    }
}

export default Navigation = withRouter(connect(null, {logout})(Navigation))
