import React, {Component} from 'react';
import {connect} from 'react-redux';
import Nav from 'react-bootstrap/Nav';
import { Link, withRouter } from "react-router-dom";
import { logout } from './actions/authActions';


class Navigation extends Component {

    handleLogout = (e) => {
        e.preventDefault();
        this.props.logout();
        this.props.history.push('/')
    }


    render() {



        const mainNav = (
           <Nav defaultActiveKey="/">
                <Nav.Link href="/">PicList</Nav.Link> | 
              <Nav.Link to="/about"> About</Nav.Link> | 
              <Nav.Link to="/login"> Log In</Nav.Link> | 
              <Nav.Link to="/signup"> Sign Up</Nav.Link> 
              <h3>
                  PicList!
              </h3>
            </Nav>
        )

        const userNav = (
            <Nav defaultActiveKey="/">
            <Nav.Link to="/">PicList</Nav.Link> | 
              <Nav.Link to="/about"> About</Nav.Link> | 
              <Nav.Link to="/saved_pics"> Saved Pics</Nav.Link> | 
              <Nav.Link to="#" onClick={(e) => this.handleLogout(e)}> Log Out</Nav.Link>
              <h3>
                  PicList!
              </h3>
            </Nav>
        );


        return (
        <>

                {this.props.isAuthenticated ? userNav : mainNav}

        </>
        )
    }
}

export default Navigation = withRouter(connect(null, {logout})(Navigation))
